import unittest
from main import *

class Test_test_Desing_Patterns_2(unittest.TestCase):
    def test_ticket(self):
        passanger = Passanger('4401 Bathurst St', '16 Hendon Ave', 'Glam', 0, 28)
        passanger.buy_ticket()
        res=Department().take_a_transport(passanger)
        self.assertEqual(res,'You take a transport!')

    def test_ticket2(self):
        passanger = Passanger('4401 Bathurst St', '16 Hendon Ave', 'Glam', 0, 28)
        res=Department().take_a_transport(passanger)
        self.assertEqual(res,'You dont have a ticket')

